class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();

        bool allzero = true;
        for (auto i : nums) if (i != 0) { allzero = false; break; }

        if (allzero) return 0;

        int l = 0, r = queries.size(), ans = -1;

        while (l <= r) {
            int mid = (l + r) / 2;
            bool flag = true;

            for (int i = 0; i < n && flag; i++) {
                if (nums[i] == 0) continue;

                vector<int> vals;
                for (int j = 0; j < mid; j++) if (queries[j][0] <= i && i <= queries[j][1]) vals.push_back(queries[j][2]);

                bitset<1001> dp;
                dp[0] = 1;

                for (auto v : vals) dp |= (dp << v);

                if (!dp[nums[i]]) flag = false;
            }

            flag ? (ans = mid, r = mid - 1) : l = mid + 1;
        }
        return ans;
    }
};

