class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size(), m = q.size();
        int l = 1, r = m, ans = INT_MAX, p = *max_element(nums.begin(), nums.end());
        if (p == 0) return 0;

        while (l <= r) {
            int mid = (l + r) / 2;
            vector<vector<int>> v1(n);

            for (int i = 0; i < mid; i++) {
                for (int j = q[i][0]; j <= q[i][1]; j++) {
                    v1[j].push_back(q[i][2]);
                }
            }

            bool ok = true;
            for (int i = 0; i < n; i++) {
                bitset<1001> dp;
                dp[0] = 1;
                for (int val : v1[i]) {
                    dp |= (dp << val);
                }
                if (!dp[nums[i]]) {
                    ok = false;
                    break;
                }
            }

            if (ok) {
                ans = min(ans, mid);
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }

        return ans == INT_MAX ? -1 : ans;
    }
};

