    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        boolean[][][] dp = new boolean[m][n][1001];
        int ans = 0;
        boolean flag=true;
        for(int num : nums){
            if(num!=0) {
                flag = false;
                break;
            }
        }
        if(flag){
            return 0;
        }
        flag = true;
        for(int j = 0;j<n;j++){
            dp[0][j][0] = true;
            if(j>=queries[0][0] && j<=queries[0][1]) {
                dp[0][j][queries[0][2]] = true;
            }

            if(!dp[0][j][nums[j]]) {
                flag=false;
            }
        }
        if(flag){
            return 1;
        }

        for(int i = 1; i < m ; i++){
            int[] query = queries[i];
            int l = query[0];
            int r = query[1];
            int val = query[2];
            for(int j = 0;j<n;j++){
                dp[i][j][0] = true;
                if(j>=l && j<=r){
                    for(int k = 1;k<=1000;k++){
                        if(k-val >= 0) {
                            dp[i][j][k] = dp[i-1][j][k] || dp[i-1][j][k-val];
                        } else {
                            dp[i][j][k] = dp[i-1][j][k];
                        }
                    }
                } else {
                    for(int k = 1;k<=1000;k++){
                        dp[i][j][k] = dp[i-1][j][k];
                    }
                }
                flag = true;
                for(int k = 0;k<n;k++) {
                    if(!dp[i][k][nums[k]]) {
                        flag=false;
                        break;
                    }
                }
                if(flag){
                    return i+1;
                }
            }
        }
        return -1;
    }

}

