class Solution {
    public int minZeroArray(int[] arrr, int[][] q) {
        int n = arrr.length;
        boolean allZero = true;
        for (int num : arrr) {
            if(num != 0) { allZero = false; break; }
        }
        if(allZero) return 0;
        boolean[][] dp = new boolean[n][1001];
        for (int i = 0; i < n; i++) dp[i][0] = true;

        for (int k = 0; k < q.length; k++) {
            int l = q[k][0], r = q[k][1], val = q[k][2];
            for (int i = l; i <= r; i++) {
                boolean[] newdp = new boolean[1001];
                for (int s = 0; s < 1001; s++) {
                    if (dp[i][s]) newdp[s] = true;
                }
                for (int s = 0; s <= 1000 - val; s++) {
                    if (dp[i][s]) newdp[s + val] = true;
                }
                dp[i] = newdp;
            }
            boolean all = true;
            for (int i = 0; i < n; i++) {
                if (!dp[i][arrr[i]]) { all = false; break; }
            }
            if(all) return k + 1;
        }
        return -1;
    }


}

