class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        l = 0
        r = len(queries)
        if set(nums) == {0}:
            return 0
        res = -1

        def isvalid(k):
            for i in range(n):
                target = nums[i]
                coins = []
                for j in range(k):
                    l, r, val = queries[j]
                    if l <= i <= r:
                        coins.append(val)

                dp = 1
                for coin in coins:
                    dp |= (dp << coin)
                    dp &= (1 << (target + 1)) - 1
                if (dp >> target) & 1 == 0:
                    return False
            return True

        while l <= r:
            m = (l+r) // 2

            if isvalid(m):
                res = m
                r = m-1
            else:
                l = m+1

        return res

