    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> num_query(n);
        for(int i = 0; i < queries.size(); ++i) {
            const vector<int>& q = queries[i];
            int l = q[0], r = q[1];
            for(int j = l; j <=r ; ++j) {
                num_query[j].push_back(i);
            }
        }

        int ans = 0;
        for(int i = 0; i < n; ++i) {
            if(nums[i] == 0) {
                continue;
            }

            unordered_set<int> vals;
            vals.insert(nums[i]);
            const vector<int>& qs = num_query[i];
            bool found = false;
            for(int j : qs) {
                int dec = queries[j][2];
                if(vals.count(dec)) {
                    found = true;
                    ans = max(ans, j + 1);
                    break;
                }
                else {
                    unordered_set<int> nexts;
                    for(int val : vals) {
                        if(val > dec) {
                            nexts.insert(val - dec);
                        }
                    }
                    for(int next : nexts) {
                        vals.insert(next);
                    }
                }
            }

            if(!found) {
                return -1;
            }
        }

        return ans;
    }
};

