class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();
        int ans = 0;
        for (int i = 0; i < n; i++) {
            vector<int> affecting;
            for (int j = 0; j < q; j++) {
                if (queries[j][0] <= i && i <= queries[j][1]) {
                    affecting.push_back(j);
                }
            }
            vector<bool> dp(nums[i] + 1, false);
            dp[nums[i]] = true;
            int cur = -1;
            for (auto qIdx : affecting) {
                if (dp[0]) break;
                int val = queries[qIdx][2];
                cur = qIdx;
                for (int j = val; j <= nums[i]; j++) {
                    if (dp[j] && j - val >= 0) {
                        dp[j - val] = true;
                    }
                }
            }
            if (!dp[0]) return -1;
            ans = max(ans, cur + 1);
        }
        return ans;
    }
};

