from sortedcontainers import SortedList
class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        N=len(nums)
        M=len(queries)

        if sum(nums) == 0:
            return 0

        low,high=1,M

        def go(k):
            arr=defaultdict(lambda : SortedList())
            for l,r,val in queries[:k]:
                for i in range(l,r+1):
                    arr[i].add(val)

            for i in range(N):
                A=arr[i]
                @cache
                def helper(i,val):
                    if val == 0:
                        return True
                    if i == len(A):
                        return val == 0
                    if A[i]>val:
                        return False
                    x=helper(i+1,val)
                    y=helper(i+1,val-A[i])
                    return x or y

                helper.cache_clear()
                if not helper(0,nums[i]):
                    return False

            return True

        while low<=high:
            mid=(low+high)//2
            if go(mid):
                high=mid-1
            else:
                low=mid+1
        ans=high+1
        if ans>M:
            ans=-1
        return ans

