    bool check(vector<int>& nums, vector<vector<int>>& queries,int mid){
        vector<vector<int>> temp(nums.size());
        for(int i = 0;i<mid;i++){
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for(int j = l;j<=r;j++){
                temp[j].push_back(val);
            }
        }
        for(int i = 0;i<nums.size();i++){
            if(nums[i] == 0){
                continue;
            }
            if(nums[i] > 0 && temp[i].size() == 0){
                return false;
            }
            int n = temp[i].size();
            vector<vector<bool>> dp(n,vector<bool>(nums[i]+1,0));
            for(int i = 0;i<n;i++){
                dp[i][0] = true;
            }
            dp[0][temp[i][0]] = true;
            for(int ind = 1;ind<n;ind++){
                for(int target = 1;target<=nums[i];target++){
                    bool nottake = dp[ind-1][target];
                    bool take = false;
                    if(temp[i][ind]<=target){
                        take = dp[ind-1][target-temp[i][ind]];
                    }
                    dp[ind][target] = take | nottake;
                }
            }
            if(dp[n-1][nums[i]] == false){
                return false;
            }
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int low = 0;
        int high = queries.size();
        int ans = -1;
        while(low <= high){
            int mid = low + (high-low)/2;
            if(check(nums,queries,mid)){
                high = mid-1;
                ans = mid;
            }
            else{
                low = mid+1;
            }
        }
        return ans;
    }
};

