class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int arraySize = nums.size();
        pair<vector<int>, vector<vector<int>>> inputPair = {nums, queries};
        vector<bitset<1001>> possibilities(arraySize);

        int index = 0;
        while (index < arraySize) {
            possibilities[index].reset();
            possibilities[index].set(0, 1);
            index++;
        }

        int isAllZero = 1;
        index = 0;
        while (index < arraySize) {
            if (nums[index] != 0) {
                isAllZero = 0;
                break;
            }
            index++;
        }
        if (isAllZero)
            return 0;

        int queryIndex = 0;
        while (queryIndex < queries.size()) {
            int startPos = queries[queryIndex][0];
            int endPos = queries[queryIndex][1];
            int shiftAmount = queries[queryIndex][2];

            index = startPos;
            while (index <= endPos) {
                possibilities[index] |= (possibilities[index] << shiftAmount);

                int bitPosition = nums[index] + 1;
                while (bitPosition < 1001) {
                    possibilities[index].reset(bitPosition);
                    bitPosition++;
                }
                index++;
            }

            int targetReached = 1;
            index = 0;
            while (index < arraySize) {
                if (!possibilities[index].test(nums[index])) {
                    targetReached = 0;
                    break;
                }
                index++;
            }
            if (targetReached)
                return queryIndex + 1;

            queryIndex++;
        }
        return -1;
    }
};
