class Solution:
    def check(self, nums: list[int], queries: list[list[int]], middle: int) -> bool:
        n: int = len(nums)
        dp: list[set[int]] = [set() for _ in range(n)]
        for i in range(n):
            dp[i].add(nums[i])
        for l, r, v in queries[:middle]:
            for i in range(l, r + 1):
                if 0 in dp[i]: continue
                for num in dp[i].copy():
                    if num >= v: dp[i].add(num - v)
        return all(0 in dp[i] for i in range(n))
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        left: int = 0
        right: int = len(queries)
        answer: int = -1
        while left <= right:
            middle: int = left + ((right - left) >> 1)
            if self.check(nums, queries, middle):
                answer = middle
                right = middle - 1
            else: left = middle + 1
        return answer

