class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int left = 0, right = queries.length, result = -1;

        while (left <= right) {
            int mid = (left + right) / 2;
            if (canFormZero(nums, queries, mid)) {
                result = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }

        return result;
    }

    private boolean canFormZero(int[] nums, int[][] queries, int k) {
        int n = nums.length;

        for (int idx = 0; idx < n; idx++) {
            int target = nums[idx];

            List<Integer> decrements = new ArrayList<>();
            for (int i = 0; i < k; i++) {
                int l = queries[i][0], r = queries[i][1], val = queries[i][2];
                if (idx >= l && idx <= r) {
                    decrements.add(val);
                }
            }

            if (!subsetSumPossible(decrements, target))
                return false;
        }

        return true;
    }

    private boolean subsetSumPossible(List<Integer> vals, int target) {
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;

        for (int val : vals) {
            for (int t = target; t >= val; t--) {
                if (dp[t - val]) dp[t] = true;
            }
        }

        return dp[target];
    }
}

