class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)
        max_k = 0

        for i in range(n):
            T = nums[i]
            if T == 0:
                current_k_i = 0
            else:
                covered_queries = []
                for j, q in enumerate(queries):
                    l, r, val = q
                    if l <= i <= r:
                        covered_queries.append((j, val))

                if not covered_queries:
                    return -1

                covered_queries.sort()
                dp = {0: -1}
                target = T

                for (j, val) in covered_queries:
                    new_dp = {}
                    for s in dp:
                        existing_j = dp[s]
                        if s in new_dp:
                            if existing_j < new_dp[s]:
                                new_dp[s] = existing_j
                        else:
                            new_dp[s] = existing_j

                    for s in dp:
                        new_s = s + val
                        if new_s > target:
                            continue
                        if new_s in new_dp:
                            if j < new_dp[new_s]:
                                new_dp[new_s] = j
                        else:
                            new_dp[new_s] = j

                    dp = new_dp

                if target not in dp:
                    return -1

                current_k_i = dp[target] + 1

            max_k = max(max_k, current_k_i)

        if max_k > m:
            return -1

        return max_k

