class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int res = -1;
        for(int i = 0; i < nums.size(); ++i)
        {
            if(nums[i] == 0)
                continue;
            int leastIdx = leastIdxSatisfy(nums, queries, i);
            if(leastIdx == -1)
                return -1;
            res = max(res, leastIdx);
        }
        return res+1;
    }


    int leastIdxSatisfy(vector<int>& nums, vector<vector<int>>& queries, int i)
    {
        int res = -1;
        vector<vector<int>> dp(queries.size(), vector<int>(nums[i]+1, -1));
        for(int idx = 0; idx < queries.size(); ++idx)
        {
            if(dpCal(i, dp, queries, idx, nums[i]) == 1)
            {
                res = idx;
                break;
            }            
        }
        return res;
    }
    int dpCal(int pos, vector<vector<int>>& dp, vector<vector<int>>& queries, int idx, int sum)  //0~idx, can it make sum become 0 after choosing some subsets?
    {
        if(dp[idx][sum] != -1)
            return dp[idx][sum];
        if(sum == 0)
        {
            return dp[idx][sum] = 1;
        }
        int l = queries[idx][0], r = queries[idx][1], val = queries[idx][2];
        if(idx == 0)
        {
            if((!(l <= pos && pos <= r)) || sum != val)  //cannot pick
                return dp[idx][sum] = 0;
            return dp[idx][sum] = 1;
        }
        if(l <= pos && pos <= r)
        {
            if(val <= sum)  //pick
            {
                if(dpCal(pos, dp, queries, idx-1, sum-val) == 1)
                {
                    return dp[idx][sum] = 1;
                }
            }
        }
        // not pick
        if(dpCal(pos, dp, queries, idx-1, sum) == 1)
        {
            dp[idx][sum] = 1;
        }
        else
        {
            dp[idx][sum] = 0;
        }
        return dp[idx][sum];
    }
};

