class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        boolean[][] dp = initializeDP(n);

        if (allZeros(nums)) return 0;

        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0], r = queries[k][1], v = queries[k][2];
            applyDecrements(dp, nums, l, r, v);

            if (allAchieved(dp, nums)) return k + 1;
        }

        return -1;
    }

    private boolean[][] initializeDP(int n) {
        boolean[][] dp = new boolean[n][1001];
        for (int i = 0; i < n; i++) {
            dp[i][0] = true;
        }
        return dp;
    }

    private boolean allZeros(int[] nums) {
        for (int num : nums) {
            if (num != 0) return false;
        }
        return true;
    }

    private void applyDecrements(boolean[][] dp, int[] nums, int l, int r, int v) {
        for (int j = l; j <= r; j++) {
            for (int pos = 1000; pos >= v; pos--) {
                if (dp[j][pos - v]) {
                    dp[j][pos] = true;
                }
            }
            for (int pos = nums[j] + 1; pos < 1001; pos++) {
                dp[j][pos] = false;
            }
        }
    }

    private boolean allAchieved(boolean[][] dp, int[] nums) {
        for (int i = 0; i < nums.length; i++) {
            if (!dp[i][nums[i]]) return false;
        }
        return true;
    }
}

