        vector<vector<pair<int,int>>>v(n);
        for(int i = 0; i < q; i++){
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for(int j = l; j <= r; j++){
                v[j].push_back({val, i + 1});
            }
        }
        int ans = -1;
        for(int i = 0; i < n; i++){
            if(nums[i] == 0){
                ans = max(ans, 0);
                continue;
            }
            set<int>st;
            st.insert(0);
            bool found = false;
            for(int j = 0; j < v[i].size(); j++){
                set<int>st1;
                for(int x:st){
                    if(x + v[i][j].first == nums[i]){
                        ans = max(ans, v[i][j].second);
                        found = true;
                        break;
                    }
                    st1.insert(x + v[i][j].first);
                }
                if(found) break;
                for(int y:st1) st.insert(y);
            }
            if(found == false) return -1;
        }
        return ans;
    }
