                if (nums[i] != 0)
                    ok = false;
            if (ok)
                return 0;
        }
        vector<vector<bool>> vs(n);
        for (unsigned i = 0; i < n; ++i) {
            vs[i].resize(nums[i] + 1);
            vs[i][0] = true;
