            auto &q = queries[i];
            unsigned l = q[0];
            unsigned r = q[1];
            unsigned v = q[2];
            for (unsigned j = l; j <= r; ++j) {
                for (unsigned t = nums[j]; t >= v; t--)
                    vs[j][t] = vs[j][t] || vs[j][t - v];
            }
            bool ok = true;
            for (unsigned j = 0; j < n; ++j)
                if (!vs[j][nums[j]])
                    ok = false;
            if (ok)
                return i + 1;
        }
        return -1;
    }
};

