class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        options = [[] for _ in range(n)]

        for ts, (ll, rr, vv) in enumerate(queries):
            for ii in range(ll, rr + 1):
                options[ii].append((vv, ts))


        def solve(arr, ttt):
            @functools.lru_cache(None)
            def dp(m, t):
                if m == 0:
                    return t == 0
                if t == 0:
                    return True
                if t < 0:
                    return False

                return dp(m - 1, t) or dp(m - 1, t - arr[m - 1][0])

            for mmm in range(0, len(arr) + 1):
                if dp(mmm, ttt):
                    if mmm == 0:
                        sol = -1
                    else:
                        sol = arr[mmm-1][1]
                    break
            else:
                sol = float('inf')
            dp.cache_clear()
            return sol

        ret = -1
        for i in range(n):
            ts = solve(options[i], nums[i])
            if ts == float('inf'):
                return -1
            ret = max(ret, ts)
        return ret + 1

