class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        left = 0
        right = len(queries)
        n = len(nums)
        res = -1
        def helper(vals, i, target):
            if not target:
                return True

            if target < 0 or i == len(vals):
                return False

            return helper(vals, i+1, target-vals[i]) or helper(vals, i+1, target)

        while left <= right:
            mid = (left+right)//2
            isZero = True
            vals = [[] for _ in range(n)]
            for i in range(mid):
                l, r, val = queries[i]
                for indx in range(l, r+1):
                    vals[indx].append(val)

            isZero = True
            for i in range(n):
                if sum(vals[i]) < nums[i] or not helper(vals[i], 0, nums[i]):
                    isZero = False
                    break

            if isZero:
                res = mid
                right = mid-1

            else:
                left = mid+1

        return res

