/**
 * @param {number[]} nums
 * @param {number[][]} queries
 * @return {number}
 */
var minZeroArray = function(nums, queries) {
  const n = nums.length;
  const m = queries.length;

  if (Math.max(...nums) === 0) return 0;

  const values = new Array(n).fill().map(() => new Array());

  for (let j = 0; j < m; j += 1) {
    const [l, r, v] = queries[j];
    for (let i = l; i <= r; i += 1) {
      values[i].push([v, j]);
    }
  }

  const earliest = new Array(n).fill(Infinity);

  for (let i = 0; i < n; i += 1) {

    const k = values[i].length;
    const dp = new Array(k + 1).fill().map(() => new Array(nums[i] + 1).fill(null));
    const f = (j, rem) => {

      if (rem === 0) {
        return values[i][j - 1][1];
      }

      // cant do
      if (j >= values[i].length) return Infinity;

      if (dp[j][rem] !== null) return dp[j][rem];

      let min = Infinity;
      // take
      if (values[i][j][0] <= rem) min = Math.min(min, f(j + 1, rem - values[i][j][0]));

      // not take
      min = Math.min(min, f(j + 1, rem));

      return dp[j][rem] = min;
    }

    if (nums[i] === 0) {
      earliest[i] = 0;
      continue;
    }
    const e = f(0, nums[i]);
    // console.log(values[0])
    // console.log(i, e)

    if (e === Infinity) return -1;
    earliest[i] = e;
  }

  // console.log(earliest)

  const res =  Math.max(...earliest);
  // if (res === 0) return 0;
  return res + 1;
};

