class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        '''
            nums: array[int]
                nums.n ~ 1..10
            queries: [li, ri, vali]
                queries.n <= 1000

                select subset in range [li, ri]
                decrease value at selected index by vali
                    => Select any indices if [li, ri] -> decrease by `vali`

            ZERO array -> all elements == 0

            RETURN MIN possible NON-negative value of `k` -> process first `k` queries
                if not found -> -1
            Naive:
                -> for each index
                    find min operation
        '''
        def can_zero_out(nums, queries_k):
            N = len(nums)
            coverage = [[] for _ in range(N)]
            for (l, r, v) in queries_k:
                for i in range(l, r + 1):
                    coverage[i].append(v)
            for i in range(N):
                target = nums[i]
                if target == 0:
                    continue
                possible_sums = {0}
                for val in coverage[i]:
                    new_sums = set(possible_sums)  # start by copying
                    for s in possible_sums:
                        new_val = s + val
                        if new_val == target:  
                            new_sums.add(new_val)
                            break
                        elif new_val < target:
                            new_sums.add(new_val)
                    possible_sums = new_sums
                    if target in possible_sums:
                        break
                if target not in possible_sums:
                    return False
            return True
        if not can_zero_out(nums, queries):
            return -1
        l, r = 0, len(queries) + 1
        while l < r:
            m = (l + r) // 2
            if can_zero_out(nums, queries[:m]):
                r = m
            else:
                l = m + 1
        return l

