class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        myStore = [set([num]) for num in nums]
        remain = len(nums) - sum([1 if num == 0 else 0 for num in nums])
        if remain == 0:
            return 0

        # print(remain)
        for j, v in enumerate(queries):
            l, r, n = v
            for i in range(l, r + 1):
