class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        # find min for each num 
        @cache
        def dfs(i,j,r):
            if r == 0:
                return 0
            if j==len(queries):
                return inf

            ret = inf
            if queries[j][0]<=i<=queries[j][1]:
                if r>=queries[j][2]:
                    # use 
                    ret=min(ret, 1+dfs(i,j+1,r-queries[j][2]))
            # not use 
            ret=min(ret, 1+dfs(i,j+1,r))
            return ret

        ret = 0
        for i, n in enumerate(nums):
            t = dfs(i,0,nums[i])
            #print(i, t)
            ret=max(ret, t)
            dfs.cache_clear()
        return ret if ret!=inf else -1

