class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if sum(nums) == 0:
            return 0

        n = len(nums)
        max_target = max(nums)
        mask = (1 << (max_target + 1)) - 1

        dp = [1] * n  # dp[i] is an integer where the jth bit is set if sum j is reachable.

        for k, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                dp[i] |= (dp[i] << val)
                dp[i] &= mask

            # Check if for every index, the target value is reachable.
            if all((dp[i] >> nums[i]) & 1 for i in range(n)):
                return k + 1  # queries are 0-indexed, so k+1 queries have been processed.

        return -1

