class Solution {
public:

    int solve(vector<pair<int, int>> &v, int sum, int i, vector<vector<int>> &dp){
        if(sum < 0) return 1e9;
        if(sum == 0) return 0;

        if(i >= v.size()) return 1e9;

        if(dp[i][sum] != -1) return dp[i][sum];

        int skip = solve(v, sum, i+1, dp);
        int pick = max(v[i].second, solve(v, sum-v[i].first, i+1, dp) );

        return dp[i][sum] = min(pick, skip);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        vector<vector<pair<int, int>>> v(n);

        int j =1;
        for(auto it : queries){
            int s = it[0], e = it[1], val = it[2];

            for(int i=s;i<=e;i++){
                v[i].push_back({val, j});
            }

            j++;
        }

        vector<int> ans(n, 1e9);
        for(int i=0;i<n;i++){
             vector<vector<int>> dp(v[i].size() + 1, vector<int>(nums[i] + 1, -1));

            ans[i] = solve(v[i], nums[i], 0, dp);
        }

        int maxi = *max_element(ans.begin(), ans.end());
        if(maxi >= 1e9) return -1;
        return maxi;
    }
};

