        int q = queries.length;
        ArrayList<Set<Integer>> list = new ArrayList<>();
        for(int i=0;i<n;i++) list.add(new HashSet<>());
        boolean ok[] = new boolean[n];
        for(int i=0;i<n;i++){
            list.get(i).add(nums[i]);
            if(nums[i]==0) ok[i] = true;
        }
        boolean yess = true;
        for(int i=0;i<n;i++) if(!ok[i]){
            yess = false;
            break;
        }
        if(yess) return 0;
        for(int i=0;i<q;i++){
            u:for(int j=queries[i][0];j<=queries[i][1];j++){
                if(ok[j]) continue;
                Set<Integer> temp = new HashSet<>();
                for(int k : list.get(j)){
                    int val = k - queries[i][2];
                    if(val==0){
                        ok[j] = true;
                        continue u;
                    }
                    else if(val < 0) continue;
                    else temp.add(val);
                }
                list.get(j).addAll(temp);
            }
            boolean yes = true;
            for(int j=0;j<n;j++){
                if(!ok[j]){
                    yes = false;
                    break;
                }
            }
            //System.out.println(list);
            if(yes) return i + 1;
        }
        return -1;
    }
}

