class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& asks) {
        int n = nums.size(), q = asks.size();

        auto rec = [](auto& self, int rem, int i, vector<int> &v,
                      vector<vector<int>> &dp) {
            if (rem == 0)
                return 1;
            if (i == v.size())
                return 0;
            int& ret = dp[rem][i];
            if (~ret)
                return ret;
            ret = self(self, rem, i + 1, v, dp);
            if (rem - v[i] >= 0)
                ret |= self(self, rem - v[i], i + 1, v, dp);
            return ret;
        };
        auto can = [&](int mid) {
            vector<vector<int>> values(n);
            for (int g = 0; g < mid; g++) {
                int l = asks[g][0];
                int r = asks[g][1];
                int val = asks[g][2];
                for (int i = l; i <= r; i++)
                    values[i].push_back(val);
            }
            for (int g = 0; g < n; g++) {
                vector<vector<int>> dp(1001, vector<int>(values[g].size(),-1));

                int temp = rec(rec, nums[g], 0, values[g], dp);
                if (temp == 0)
                    return 0;
            }
            return 1;
        };
        int st = 0, end = q, mid, ans = -1;
        while (st <= end) {
            mid = (st + end) / 2;
            if (can(mid)) {
                ans = mid;
                end = mid - 1;
            } else
                st = mid + 1;
        }
        return ans;
    }
};

