class Solution {
public:
    vector<int> v;
    unordered_map<int, unordered_map<int, int>> dp;

    int helper(int idx, int target, const vector<vector<int>>& queries) {
        if (target == 0) return -1;
        if (idx >= v.size()) return INT_MAX;

        if (dp[idx].find(target) != dp[idx].end()) return dp[idx][target];

        int query_idx = v[idx];
        int val = queries[query_idx][2];

        int result = INT_MAX;

        int skip = helper(idx + 1, target, queries);
        if (skip != INT_MAX) {
            result = min(result, skip);
        }

        if (val <= target) {
            int use = helper(idx + 1, target - val, queries);
            if (use != INT_MAX) {
                result = min(result, max(query_idx, use));
            }
        }

        dp[idx][target] = result;
        return result;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int ans = 0;

        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) continue;

            v.clear();
            for (int j = 0; j < m; j++) {
                if (queries[j][0] <= i && i <= queries[j][1]) {
                    v.push_back(j);
                }
            }

            if (v.empty()) {
                return -1;
            }

            dp.clear();
            int res = helper(0, nums[i], queries);

            if (res == INT_MAX) {
                return -1;
            }

            ans = max(ans, res + 1);
        }

        if (ans <= m) {
            return ans;
        }

        return -1;
    }
};

