import java.util.*;

class Solution {
    private boolean canSumTo(List<Integer> values, int target, int idx, HashMap<String,Boolean> map) {
        if (target == 0) return true;
        if (idx >= values.size()) return false;
        String key = idx + " # " + target;
        if (map.containsKey(key)) return map.get(key);
        if (canSumTo(values, target, idx + 1, map)) {
            map.put(key, true);
            return true;
        }
        int current = values.get(idx);
        if (current <= target && canSumTo(values, target - current, idx + 1, map)) {
            map.put(key, true);
            return true;
        }

        map.put(key, false);
        return false;
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, m = queries.length;
        int st =0,end = m;
        int ans = -1;
        while(st <=end){
            int mid = (st+end)/2;
            boolean flag = true;
            for (int i = 0; i < n; i++) {
                List<Integer> list = new ArrayList<>();
                for (int j = 0; j < mid; j++) {
                    int l = queries[j][0], r = queries[j][1], val = queries[j][2];
                    if (l <= i && i <= r) {
                        list.add(val);
                    }
                }
                HashMap<String, Boolean> map = new HashMap<>();
                if (!canSumTo(list, nums[i], 0, map)) {
                    flag = false;
                    break;
                }
            }
            if (flag) {
                ans = mid;
                end = mid-1;
            }
            else st = mid+1;
        }
        return ans;
    }

}

