#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0, r = queries.size() - 1, ans = -1, n = nums.size();

        if (all_of(nums.begin(), nums.end(), [](int x) { return x == 0; })) 
            return 0;

        while (l <= r) {
            int mid = (l + r) / 2;
            vector<vector<int>> v(n);

            for (int i = 0; i <= mid; i++) {
                for (int j = queries[i][0]; j <= queries[i][1]; j++) {
                    v[j].emplace_back(queries[i][2]);
                }
            }

            vector<int> a = nums;
            bool flag= true;

            for (int i = 0; i < n; i++) {
                unordered_set<int> dp = {0};

                for (int val : v[i]) {
                    vector<int> tmpsum;
                    for (int s : dp) {
                        tmpsum.push_back(s + val);
                    }
                    dp.insert(tmpsum.begin(), tmpsum.end());
                }

                if (dp.count(nums[i])) {
                    a[i] = 0;
                } else {
                    flag= false;
                }
            }

            if (flag) {
                ans = mid + 1;
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }

        return ans;
    }
};

