class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int max = 0;
        for(int i = 0; i < n; i++) {
            if(nums[i] == 0)
                continue;
            int need = f(i, nums[i], queries);
            if(need == -1)
                return -1;
            max = Math.max(need, max);
        }
        return max;
    }

    public int f(int ind, int n, int[][] q) {
        int qs = q.length;
        int[] dp = new int[n+1];
        Arrays.fill(dp, -1);
        dp[n] = 0;
        for(int i = 0; i < qs && dp[0] == -1; i++) {
            int[] ndp = new int[n+1];
            for(int j = n; j >= 0; j--) {
                ndp[j] = dp[j];
            }
            if(q[i][0] <= ind && ind <= q[i][1]) {
                for(int j = n; j >= 0; j--) {
                    if(dp[j] == -1)
                        continue;
                    int nind = j - q[i][2];
                    if(nind < 0)
                        continue;
                    ndp[nind] = i;
                }
            }
            dp = ndp;
        }
        return dp[0] == -1 ? -1 : dp[0] + 1;
    }
}

