class Solution {
public:
    int f(int n, int i, vector<vector<int>>& queries, int k) {
        bitset<1005> bt;
        if (n == 0) return 1;
        bt[0] = 1;

        for (int j = 0;j<=k;j++) {
            if (i >= queries[j][0] && i <= queries[j][1]) {
                bt = bt | (bt << queries[j][2]);

                if (bt[n] == 1) return 1;
            }
        }
        return -1;
    }

    bool check(vector<vector<int>>& queries, vector<int>& nums, int k) {
        for (int i=0;i<nums.size();i++) {
            int x = f(nums[i], i, queries, k);
            if (x == -1) return false;
        }

        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0, r = queries.size() - 1, ans = -1;
        int sum = 0;
        for(int i=0;i<nums.size();i++) {
            sum += nums[i];
        }

        if (sum == 0) return 0;

        while(l <= r) {
            int mid = l + (r - l) / 2;

            if (check(queries, nums, mid)) {
                ans = mid;
                r = mid - 1;
            }
            else l = mid + 1;
        }

        if (ans == -1) return ans;
        return ans + 1;
    }
};

