class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(), maxi = *max_element(nums.begin(), nums.end());
        vector<vector<bool>> dp(n, vector<bool>(maxi+1));
        int zeros=0;
        for (int i=0; i<n; ++i) {
            dp[i][nums[i]] = true;
            zeros += nums[i] == 0;
        }
        if (zeros == n) return 0;
        int nqueries=queries.size();
        for (int i=0; i<nqueries; ++i) {
            int l=queries[i][0];
            int r=queries[i][1];
            int val=queries[i][2];
            for (int j=l; j<=r; ++j) {
                for (int k=val; k<=maxi; ++k) {
                    if (dp[j][k]) dp[j][k-val] = true;
                }
            }
            zeros=0;
            for (int j=0; j<n; ++j) {
                zeros += dp[j][0] == true;
            }
            if (zeros == n) return i+1;
        }
        return -1;
    }
};
