class Solution {
public:
    int f(vector<int>& nums, vector<vector<int>>& queries,int idx,int num,int nidx,vector<vector<int>> &dp){
        if(num==0) return idx;
        if(idx==queries.size()) return INT_MAX;
        if(dp[num][idx]!=-1) return dp[num][idx];
        int a=INT_MAX;
        if(nidx>=queries[idx][0] && nidx<=queries[idx][1] && num>=queries[idx][2]){
            a=f(nums,queries,idx+1,num-queries[idx][2],nidx,dp);
        }
        int b=f(nums,queries,idx+1,num,nidx,dp);
        return dp[num][idx] = min(a,b);
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q=queries.size();
        int k=INT_MIN;
        for(int i=0;i<nums.size();i++){
            vector<vector<int>> dp(nums[i]+1,vector<int> (q,-1));
            int temp=f(nums,queries,0,nums[i],i,dp);
            if(temp==INT_MAX) return -1;
            k=max(k,temp);
        }
        return k;
    }
};

