class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        Q = len(queries)
        res = []

        for i, num in enumerate(nums):
            # Create a DP table with dimensions (Q+1) x (num+1)
            dp = [[inf] * (num + 1) for _ in range(Q + 1)]

            dp[Q][0] = Q


            for q in range(Q - 1, -1, -1):
                l, r, v = queries[q]
                for cur in range(num + 1):
                    if cur == 0:

                        dp[q][0] = q
                    else:

                        if not (l <= i <= r):
                            dp[q][cur] = dp[q + 1][cur]
                        else:

                            skip = dp[q + 1][cur]

                            take = dp[q + 1][cur - v] if cur >= v else inf
                            dp[q][cur] = min(skip, take)
            res.append(dp[0][num])

        ans = max(res)
        return ans if ans != inf else -1

