class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = -1;

        int n = nums.size();

        for (int i = 0; i < n; i++) {
            vector<vector<int>> dec;
            for (int idx = 0; idx < queries.size(); idx++) {
                auto &q = queries[idx];
                if (q[0] <= i && q[1] >= i) {
                    if (q[2] <= nums[i]) {
                        dec.push_back({q[2], idx+1});
                    }
                }
            }
            int k = findTarget(nums[i], dec);
            if (k == -1) {
                return -1;
            } else {
                if (k > ans)
                    ans = k;
            }
        }

        return ans;
    }

    int findTarget(int n, vector<vector<int>> &dec) {
        if (n == 0)
            return 0;
        unordered_set<int> here;
        for (auto &d: dec) {
            if (d[0] == n)
                return d[1];
            unordered_set<int> tmp;
            for (auto h: here) {
                if (h + d[0] == n) {
                    return d[1];
                } else if (h + d[0] < n) {
                    tmp.insert(h + d[0]);
                }
            }
            here.insert(d[0]);
            for (auto t: tmp) {
                here.insert(t);
            }
        }
        return -1;
    }
};

