class Solution {
public:
    bool rec(const vector<int>& v, int idx, int rem, vector<vector<int>>& dp) {
        if (rem == 0) return true;
        if (idx == v.size()) return false;
        if (dp[idx][rem] != -1) 
            return dp[idx][rem] == 1;
        bool no = rec(v,idx+1,rem,dp);
        bool yes = false;
        if (rem >= v[idx]) yes = rec(v,idx+1,rem-v[idx],dp);
        dp[idx][rem] = (yes || no) ? 1 : 0;
        return yes || no;
    }

    bool okay(vector<int>& v, int tgt) {
        int n = v.size();
        vector<vector<int>> dp(n+1,vector<int>(tgt+1,-1));
        return rec(v,0,tgt,dp);
    }

    bool ok(vector<int>& nums,vector<vector<int>>& q,int mid){
        int n = nums.size();
        unordered_map<int,vector<int>> mp;
        for (int i=0;i<mid;i++){
            for (int j=q[i][0];j<=q[i][1];j++) mp[j].push_back(q[i][2]);
        }
        bool iska = true;
        for (int i=0;i<n;i++){
            if (!okay(mp[i],nums[i])) {
                iska = false;
                break;
            }
        }
        return iska;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        int lo = 0, hi = q.size();
        while(hi-lo > 1){
            int mid = (hi+lo)/2;
            if (ok(nums,q,mid)){
                hi = mid;
            }
            else{
                lo = mid;
            }
        }
        if (ok(nums,q,lo)) return lo;
        if (ok(nums,q,hi)) return hi;
        return -1;
    }
};

