class Solution {
public:
    bool isPossible(vector<int>& nums, int target) {
        if (target == 0) return true;
        vector<bool> dp(target + 1, false);
        dp[0] = true;

        for (int num : nums) {
            for (int j = target; j >= num; j--) {
                dp[j] = dp[j] || dp[j - num];
            }
        }

        return dp[target];
    }

    bool solve(vector<int>& nums, vector<vector<int>>& queries, int cnt) {
        int n = nums.size();

        for (int i = 0; i < n; i++) {
            vector<int> temp;
            for (int j = 0; j <= cnt; j++) {
                int l = queries[j][0], r = queries[j][1], val = queries[j][2];
                if (i >= l && i <= r) {
                    temp.push_back(val);
                }
            }
            if (!isPossible(temp, nums[i])) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        bool flag = true;
        for(int i=0;i<nums.size();i++) if(nums[i]!=0) flag=false;
        if(flag) return 0;
        for (int i = 0; i < queries.size(); i++) {
            if (solve(nums, queries, i)) return i + 1;
        }
        return -1;
    }
};

