class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def possible(k):
            def subset_sum(i, _sum):
                key = (i, _sum)
                if key in memo:
                    return memo[key]
                if _sum == 0:
                    memo[key] = True
                elif i < 0:
                    memo[key] = False
                else:
                    memo[key] = subset_sum(i-1, _sum) or subset_sum(i-1, _sum - val_list[i])
                return memo[key]
            decrements = [[] for _ in range(n)]
            for j in range(k):
                l, r, val = queries[j]
                for i in range(l, r+1):
                    decrements[i].append(val)
            for i, val_list in enumerate(decrements):
                memo = {}
                if not subset_sum(len(val_list)-1, nums[i]):
                    return False
            return True
        n, m = len(nums), len(queries)
        l, h = 0, m+1
        while l < h:
            mid = (l + h) // 2
            if possible(mid):
                h = mid
            else:
                l = mid + 1
        return -1 if l == m+1 else l

