class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if(max(nums)==0):
            return 0
        n = len(nums)
        l = 0 
        r = len(queries)-1
        ans = r + 1
        isPoss=  False
        while(r>=l):
            # print('-----',l,r)
            m = l + (r-l)//2
            li = [{0:1} for _ in range(n)]
            # print(li)

            for i in range(m+1):
                p,q,rr = queries[i]
                for ind in range(p,q+1):
                    ttmp = list(li[ind].keys())
                    for k in ttmp:
                        if(k+rr<=nums[ind]):
                            li[ind][k+rr] = 1

            # for i in li:
                # print(m,i)

            poss= True
            for i in range(n):
                if(nums[i] not in li[i]):
                    poss = False
                    break

            if(poss):
                ans = min(ans,m+1)
                isPoss = True
                r = m-1
            else:
                l = m+1

            # print('last',l,r)

        if(isPoss):
            return ans
        return -1

