class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        TreeSet<Integer>[] trees = new TreeSet[nums.length];
        for(int i=0; i<nums.length; i++) {
            trees[i] = new TreeSet<>();
            trees[i].add(nums[i]);
        }
        for(int q=0; q<queries.length; q++) {
            if(isAllZero(trees)) {
                return q;
            }
            decrement(trees, queries[q][0], queries[q][1], queries[q][2]);
        }
        return isAllZero(trees) ? queries.length : -1;
    }

    private void decrement(TreeSet<Integer>[] trees, int start, int end, int val) {
        for(int i=start; i<=end; i++) {
            List<Integer> list = new ArrayList<>();
            for(int x: trees[i]) {
                if(x-val>=0) {
                    list.add(x-val);
                }
            }
            trees[i].addAll(list);
        }
    }

    private boolean isAllZero(TreeSet<Integer>[] trees) {
        for(int i=0; i<trees.length; i++) {
            if(trees[i].first()>0) {
                return false;
            }
        }
        return true;
    }
}

