class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int beg = 0, end = queries.size();
        int res = -1;
        while(beg <= end){
            int mid = beg + (end-beg)/2;
            if(isPossible(nums, queries, mid)){
                res = mid;
                end = mid-1;
            }
            else{
                beg = mid+1;
            }
        }
        return res;
    }

    bool isPossible(vector<int>& nums, vector<vector<int>>& queries, int ind){
        vector<vector<int>> candidates(nums.size());
        for(int i = 0; i < ind; i++){
            auto q = queries[i];
            for(int i = q[0]; i <= q[1]; i++){
                candidates[i].push_back(q[2]);
            }
        }
        int count = 0;
        for(int i = 0; i < nums.size(); i++){
            if(isPossible(nums[i], candidates[i])){
                count++;
            }
        }
        return count == nums.size();
    }

    bool isPossible(int target_sum, vector<int> arr){
        int n = arr.size();
        vector<vector<bool>> dp(n + 1, vector<bool>(target_sum + 1, false));

        for (int i = 0; i <= n; ++i) {
            dp[i][0] = true;
        }

        for (int i = 1; i <= n; ++i) {
            for (int j = 1; j <= target_sum; ++j) {
                if (arr[i - 1] <= j) {
                    dp[i][j] = dp[i - 1][j] || dp[i - 1][j - arr[i - 1]];
                } else {
                    dp[i][j] = dp[i - 1][j];
                }
            }
        }

        return dp[n][target_sum];
    }
};

