class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        def is_formable(counter: List[int], target: int) -> bool:
            dp = [False] * (target + 1)
            dp[0] = True

            for num, count in enumerate(counter):
                if count == 0:
                    continue
                for t in range(target, num - 1, -1):
                    for c in range(1, count + 1):
                        if c * num > t:
                            break
                        dp[t] |= dp[t - c * num]
                        if dp[target]:
                            return True
            return dp[target]

        def is_valid(k: int) -> bool:
            counter = [0] * 11
            events = []

            for i in range(k):
                l, r, x = queries[i]
                events.append((l, x))
                events.append((r + 1, -x))

            events.sort()

            j = 0
            for i, num in enumerate(nums):
                while j < len(events) and events[j][0] == i:
                    _, val = events[j]
                    counter[abs(val)] += 1 if val > 0 else -1
                    j += 1

                if not is_formable(counter, num):
                    return False

            return True

        if not is_valid(len(queries)):
            return -1

        left, right = 0, len(queries)
        while left < right:
            mid = (left + right) // 2
            if is_valid(mid):
                right = mid
            else:
                left = mid + 1

        return left

