class Solution {
public:
    bool helper(int k, vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        // For each position, check if we can exactly "pay" the required value using temp from first k queries.
        for (int pos = 0; pos < n; pos++) {
            int t = nums[pos];
            if (t == 0)
                continue;

            vector<int> temp;
            int s = 0;
            for (int j = 0; j < k; j++) {
                if (pos >= queries[j][0] && pos <= queries[j][1]) {
                    temp.push_back(queries[j][2]);
                    s += queries[j][2];
                }
            }
            if (s < t)
                return false;
            bitset<100001> ps;
            ps.reset();
            ps[0] = 1;
            for (int coin : temp) {
                ps |= (ps << coin);
            }
            if (!ps[t])
                return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = queries.size();
        bool zeroo = true;
        for (int num : nums) {
            if (num != 0) {
                zeroo = false;
                break;
            }
        }
        if (zeroo)
            return 0;
        int lo = 1, hi = m, ans = -1;
        while (lo <= hi) {
            int mid = lo + (hi - lo) / 2;
            if (helper(mid, nums, queries)) {
                ans = mid;
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }
        return ans;
    }
};

