class Solution {
private:
    int findRes(vector<vector<int>> &queries, int num, int idx, int i, vector<vector<int>> &dp) {
        int n = queries.size();
        if(num == 0) return idx;
        if(idx == n) return -1;
        if(num < 0) return -1;
        if(dp[num][idx] != -2) return dp[num][idx];
        int l = queries[idx][0], r = queries[idx][1], val = queries[idx][2];
        int res = i >= l && i <= r ? findRes(queries, num - val, idx+1, i, dp) : -1;

        int res2 = findRes(queries, num, idx+1, i, dp);
        if(res2 != -1) {
            if(res == -1) res = res2;
            else res = min(res, res2);
        }
        return dp[num][idx] = res;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int mx = -1, n = nums.size(), m = queries.size();
        for(int i = 0; i<n; i++) {
            vector<vector<int>> dp(1001, vector<int>(m, -2));
            int res = findRes(queries, nums[i], 0, i, dp);
            if(res == -1) return -1;
            mx = max(mx, res);
        }

        return mx;
    }
};

