class Solution {
public:

    static bool comp(const pair<int, int> &a, const pair<int, int> &b){
        return a.first < b.first;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<int> dec(n, 0); 
        bool cant = false;

        for (int j = 0; j < n; j++) {
            if (nums[j] == 0) {
                dec[j] = 0; 
                continue;
            }

            vector<pair<int, int>> ranges; 
            for (int i = 0; i < m; i++) {
                int l = queries[i][0], r = queries[i][1], val = queries[i][2];
                if (j >= l && j <= r) {
                    ranges.emplace_back(i, val);
                }
            }

            sort(ranges.begin(), ranges.end(),comp);

            bitset<2000001> dp;
            dp.set(1000000); 

            bool achieved = false;
            for (const auto& [i, val] : ranges) {
                if (val > 0) {
                    dp |= (dp << val); 
                } 
                else if (val < 0) {
                    dp |= (dp >> (-val)); 
                }

                if (dp.test(1000000 + nums[j])) {
                    dec[j] = i + 1; 
                    achieved = true;
                    break;
                }
            }

            if (!achieved) {
                cant = true; 
                break;
            }
        }

        if (cant){
            return -1;
        }

        int answer = *max_element(dec.begin(), dec.end());
        return answer;
    }
};

