class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();
        bool zero = true;
        for (int x : nums) {
            if (x != 0) { zero = false; break; }
        }
        if(zero) return 0;

        vector<int> numscpy = nums;
        int lo = 1, hi = q, ans = -1;
        while(lo <= hi) {
            int mid = lo + (hi - lo) / 2;
            bool poss = true;
            for (int i = 0; i < n && poss; i++) {
                int target = numscpy[i];
                if(target == 0) continue;
                bitset<1001> dp;
                dp.reset();
                dp[0] = 1;
                for (int j = 0; j < mid; j++) {
                    int l = queries[j][0], r = queries[j][1], v = queries[j][2];
                    if(l <= i && i <= r) {
                        dp |= (dp << v);
                    }
                }
                if(!dp[target]) poss = false;
            }
            if(poss) { ans = mid; hi = mid - 1; }
            else { lo = mid + 1; }
        }
        return ans;
    }
};

