class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if sum(nums) == 0:
            return 0

        def check(k):
            for i, n in enumerate(nums):
                if n == 0:
                    continue
                if not checkSum(i, k):
                    return False
            return True

        @cache
        def checkSum(idx, k):
            n = nums[idx]
            l = []
            for j in range(k):
                if queries[j][0] <= idx <= queries[j][1]:
                    if queries[j][2] == n:
                        return True
                    if queries[j][2] > n:
                        continue
                    l.append(queries[j][2])

            @cache
            def dfs(c, i):
                if c < 0:
                    return False
                if c == 0:
                    return True
                if i == len(l):
                    return False
                return dfs(c - l[i], i + 1) or dfs(c, i + 1)

            return dfs(n, 0)


        l = 1
        r = len(queries)

        # print(check(1))
        while l < r:
            mid = l + (r - l) // 2
            if check(mid):
                r = mid
            else:
                l = mid + 1

        return l if check(l) else -1

