class Solution {
public:
    int minZeroArray(vector<int>& arr, vector<vector<int>>& qu) {
        unordered_map<int, int> mp;

        for (int i = 0; i < arr.size(); i++) {
            if (arr[i] == 0) {
                mp[i] = 1;
            }
        }
        if (mp.size() == arr.size())
            return 0;

        int idx = 0;
        vector<vector<int>> dp(arr.size(), vector<int>(1001, 0));

        for(auto &it : dp) {
            it[0] = 1; 
        }

        for (auto it : qu) {
            vector<vector<int>> temp = dp;

            for (int i = it[0]; i <= it[1]; i++) {
                if (mp.count(i))
                    continue;

                for (int j = 0; j <= arr[i]; j++) {
                    if (dp[i][j] == 1 && j  <= arr[i] - it[2]) 
                        temp[i][j + it[2]] = 1;
                }
                if (temp[i][arr[i]] == 1) mp[i] = 1;

            }
            dp = temp;

            ++idx;
            if (mp.size() == arr.size())
                return idx;
        }
        return -1;
    }
};

